import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';

class Two extends StatefulWidget {
  const Two({super.key});

  @override
  _TwoState createState() => _TwoState();
}

class _TwoState extends State<Two> {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('The Neighborhood'),
        backgroundColor: const Color.fromARGB(255, 133, 137, 137),
      ),
      
        body: GridView.builder(
        padding: const EdgeInsets.all(16.0),
        gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
          crossAxisCount: 2,
          crossAxisSpacing: 16.0,
          mainAxisSpacing: 16.0,
        ),
        itemCount: 6, // 6 produk
        itemBuilder: (context, index) {
          // Daftar produk dan harga
          List<Map<String, String>> products = [
            {'name': 'Jaket', 'price': 'Rp 250k', 'imageUrl': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSK7KdSOWudLzhqeyWBi1g_nx34_HxUEjwp_A&s'},
            {'name': 'Baju', 'price': 'Rp 150k', 'imageUrl': 'data:image/jpeg;base64,'},
            {'name': 'Celana', 'price': 'Rp 200k', 'imageUrl': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTcnoJZ2AphIU4CmLgkkZ0hTpUCKxcwY3RzJA&s'},
            {'name': 'Jam Tangan', 'price': 'Rp 500k', 'imageUrl': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ1VkeqC38QkD93gsTKeTkdgZuKu2kt-awWdA&s'},
            {'name': 'Tas', 'price': 'Rp 400k', 'imageUrl': 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT9o6OSc2-ldipYYdYppaxk4_g9EXi40ZLh5g&s'},
            {'name': 'Parfum', 'price': 'Rp 350k', 'imageUrl': 'data:image/jpeg;base64,'},
          ];

          return _buildProductCard(products[index]['name']!, products[index]['price']!, products[index]['imageUrl']!);
        },
      ),
    );
  }

  Widget _buildProductCard(String productName, String price, String imageUrl) {
    return Card(
      elevation: 4,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(15)),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.center,
        children: [
          Expanded(
            child: Image.network(imageUrl, fit: BoxFit.cover),
          ),
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: Column(
              children: [
                Text(
                  productName,
                  style: const TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
                ),
                Text(
                  price,
                  style: const TextStyle(fontSize: 16, color: Colors.green),
                ),
              ],
            ),
          ),
          CupertinoButton.filled(
            child: const Text('Beli'),
            onPressed: () {
              // Aksi pembelian
            },
          ),
        ],
      ),
    );
  }
}
